```
function file_lock($name)
{
   $f = fopen($name, 'w');
   if ($f === false)
      return false;

   $ret = flock($f, LOCK_EX);
   if ($ret === false)
      return false;

   return $f;
}

function file_unlock($f)
{
   flock($f, LOCK_UN);
   fclose($f);
}
